import axios from "axios";
const headers = {
	"Content-Type": "application/json"
};
const burl = "http://localhost:8800";

export default {
	login: function(email, password) {
		//create a POST http request
		return axios.post(
			`${burl}/login`,
			{
				email,
				password
			},
			{
				headers: headers
			}
		);
	},
	signup: function(send) {
		return axios.post(`${burl}/signup`, send, { headers: headers });
	},

	isAuth: function() {
		let tk = localStorage.getItem("token") !== null ;
		return tk !== null && tk !== undefined;
	},
	logout: function() {
		localStorage.clear();
	},
	getToken: function() {
		return localStorage.getItem("token");
	},
	getUserInfo: async function() {
		console.log("get user data start");
		if (this.isAuth){
			console.log("Is log: true");
			let tk = this.getToken();
			console.log("tk: " + tk);
			return await axios.post(`${burl}/info`, {params: {
				  token: tk,
				}}, {headers: headers}).then(()=> console.log("THEN"));
		}
		return null;
	}
};
