{
	constructor(props)
	{
		super(props);
		this.state = {
			card_list: this.load_cards(),
			flipped: [],
			nb_row: 5,
			nb_col: 6,
		};
		this.clickOnCard.bind(this);
	}
	test_match()
	{
		if (this.state.flipped[0].id === this.state.flipped[1].id)
		{
			console.log("Match!");
			return true;
		}
		console.log("No__ match");
		return false;

	}
	updateFruit(fruit)
	{
		//récup une copie de liste de fruit
		const lst = [...this.state.card_list];
		//récupere l'index du fruit a changer
		const ndx = lst.findIndex(f => f.k === fruit.k);
		//met a jour le fruit dans la liste
		lst[ndx] = fruit;
		//update state
		this.setState({card_list: lst})
	}
	getIndex(list, obj)
	{
		return list.findIndex(f => f.k === obj.k);
	}

	clickOnCard(fruit)
	{
		if (fruit.visible)
			return;
		console.log("set visible: key=" + fruit.k + " et id=" + fruit.id);
		fruit.visible = true;
		//ajoute le fruit à flipped
		this.setState({
			flipped: [this.state.flipped, fruit],
		})
		this.updateFruit(fruit);
