import './Memory.css';

import React from 'react';
import Grid from '@material-ui/core/Grid';
import Square from './Square'
import img_loader from './images'
import { GridList } from '@material-ui/core';


function FormRow(props) {

    var row = [];
    for (let i = 0; i < props.row_size; i++)
    {
      row.push(<Square fruit={props.fruit}/>)
    }
	return (
		<React.Fragment>
		{row}
		</React.Fragment>
	);
}
function shuffleArray(array) {
	let i = array.length - 1;
	for (; i > 0; i--) {
	  const j = Math.floor(Math.random() * (i + 1));
	  const temp = array[i];
	  array[i] = array[j];
	  array[j] = temp;
	}
	return array;
  }

function Board(props)
{
	const col_size = Math.sqrt(props.donne.length);
	console.log("col size = " + col_size);
	const grid = [];
	let i = 0;
	for (; i < col_size; i++)
	{
		grid.push(<FormRow row_size={col_size}/>);
	}
	grid.push(<FormRow row_size={props.donne.length - (i * col_size)}/> )
	return (
		grid
	);
}
class Memory extends React.Component
{
  constructor()
  {
	super();
	this.state = {
		card_list: this.load_cards(),
		nb_row: 5,
		nb_col: 6,
	};
  }
  load_cards()
	{
		const fruits = img_loader();
		const cards = [];
		let i = 0;
		fruits.map((fruit)=> {
				cards.push(<square key={i++} fruit={fruit}/>);
				cards.push(<square key={i++}fruit={fruit}/>);
		});
		console.log("set a donne of " + cards.length + " cards");
		console.log("last key aloued:" + i);
		return shuffleArray(cards);
	}

  render(){
	return (
		<Grid container item xs={12} spacing={3}>
        <Board donne={this.state.card_list}/>
		</Grid>
		)}
}
export default Memory;
