import axios from "axios";
import jwt from 'jwt-simple';

const headers = {
	"Content-Type": "application/json"
};
const burl = "http://localhost:8800";

function login(name, password) {
	//create a POST http request
	return axios.post(
		`${burl}/login`,
		{
			name,
			password
		},
		{
			headers: headers
		}
	);
}
function signup(send) {
	return axios.post(`${burl}/signup`, send, { headers: headers });
}

function isAuth() {
	const tk = localStorage.getItem("token");
	if (tk === null || tk === undefined)
		return false;
	const {expire} = jwt.decode(tk);
	const ret = Date.now() >= expire;
	console.log("IsAuth: " + ret);
	return ret;
}
function logout() {
	localStorage.clear();
	window.location = "/";
}
function getToken() {
	const tk = localStorage.getItem("token");
	return tk;
}
function updateToken()
{
	return axios.put(`${burl}/updateToken`, {token:getToken()}, {headers:headers});
}
function getUserInfo() {
	if (this.isAuth){
		let token = this.getToken();
		return axios.get(`${burl}/info`, {headers: headers, params: {token : token} }).catch(error => {
			if (error.response && error.response.status === 408)
			{
				console.log("Token expired");
				logout();
			}
		});
	}
	return null;
}
function looseTheGame()
{
	return axios.post(`${burl}/loose`, {}, {headers: headers});
}

function sendScore(result, time_elapsed)
{
	localStorage.setItem("last", result);
	console.log("set result to:" + result);
	window.location = "/endgame"
	return axios.put(`${burl}/result`, {token: this.getToken(), result: result, score: time_elapsed}, {headers: headers})
}
function getScores(scope)
{
	if (this.isAuth){
		const token = this.getToken();
		return axios.get(`${burl}/scores`, {headers: headers, params: {token: token, scope: scope,}})
			.then((res) => {return res.data.scores})
			.catch(err => console.log("ERROR= " + err));
	}
}


const API = {
	signup,
	login,
	logout,
	isAuth,
	getToken,
	updateToken,
	getUserInfo,
	looseTheGame,
	sendScore,
	getScores
}
export default API;
