		lst[ndx] = fruit;
		//update state
		this.setState({card_list: lst})
	}

	getFruitIndex(fruit)
	{
		return this.state.card_list.findIndex(f => f.k === fruit.k);
	}

	getFruit(ndx)
	{
		console.log("--" + ndx + " - " + this.state.card_list[ndx]);
		return this.state.card_list[ndx];
	}

	clickOnCard(fruit)
	{
		if (fruit.visible)
			return;
		console.log("set visible: key=" + fruit.k + " et id=" + fruit.id);
		//set fruit visible
		fruit.visible = true;
		this.updateFruit(fruit);
		//save flipped card
		const flip_cnt = this.state.flipped.length;
		this.setState(state => ({...state, flipped: [...this.state.flipped, this.getFruitIndex(fruit)]}));

		console.log("flipped:" + this.state.flipped);
