import "./Menu.css";

import {useState} from 'react'
import {Link} from 'react-router-dom'
import {List} from '@material-ui/core'
import Button from '@material-ui/core/Button'
import Radio from '@material-ui/core/Radio'
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControl from '@material-ui/core/FormControl'
import FormControlLabel from '@material-ui/core/FormControlLabel'
import FormLabel from '@material-ui/core/FormLabel'


function Menu()
{
	const [nb_card, setNbCard] = useState("5");

	const handleChange = (event) => {
		console.log("NB card =" + nb_card);
		console.log("event.target.menu=" + event.target.value);
		setNbCard(event.target.value);
	  };
	return (
		<div class="extern">
			<div class="intern">
				<h2>Menu</h2>
				<FormControl component="fieldset">
					<FormLabel component="legend">Nombre de paires:</FormLabel>
					<RadioGroup row aria-label="gender" name="gender1" value={nb_card} onChange={handleChange}>
						<FormControlLabel value={"5"} control={<Radio />} label="5" />
						<FormControlLabel value={"7"} control={<Radio />} label="7" />
						<FormControlLabel value={"8"} control={<Radio />} label="8" />
						<FormControlLabel value={"10"} control={<Radio />} label="10" />
						<FormControlLabel value={"12"} control={<Radio />} label="12" />
						<FormControlLabel value={"15"} control={<Radio />} label="15" />
					</RadioGroup>
				</FormControl>
				<List>

				</List>
				<div>
				<Link to={{
					pathname: "/game",
					data: nb_card, }}>
					<Button variant="contained" color="primary">Let's Play!</Button>
				</Link>
				</div>
			</div>
		</div>);	
}
export default Menu;
