import './Memory.css';

import React from 'react';
import Grid from '@material-ui/core/Grid';
import Square from './Square'
import img_loader from './images'
import { GridList } from '@material-ui/core';


function FormRow(props) {

    var row = [];
    for (let i = props.start; i < props.start + props.row_size; i++)
    {
	  row.push(props.donne[i])
		console.log("fill row with:" + i);
    }
	return (
		<Grid container item xs={13} spacing={2} direction='column'>
		{row}
		</Grid>
	);
}
function shuffleArray(array) {
	let i = array.length - 1;
	for (; i > 0; i--) {
	  const j = Math.floor(Math.random() * (i + 1));
	  const temp = array[i];
	  array[i] = array[j];
	  array[j] = temp;
	}
	return array;
  }
  function shuffle(array) {
	var currentIndex = array.length, temporaryValue, randomIndex;

	// While there remain elements to shuffle...
	while (0 !== currentIndex) {

	  // Pick a remaining element...
	  randomIndex = Math.floor(Math.random() * currentIndex);
	  currentIndex -= 1;

	  // And swap it with the current element.
	  temporaryValue = array[currentIndex];
	  array[currentIndex] = array[randomIndex];
	  array[randomIndex] = temporaryValue;
	}

	return array;
  }

function Board(props)
{
	const col_size = Math.floor(Math.sqrt(props.donne.length));
	console.log("col size = " + col_size);
	const grid = [];
	let i = 0;
	for (; i < col_size; i++)
	{
		grid.push(<FormRow donne={props.donne} start={i * col_size} row_size={col_size}/>);
		console.log("new row");
	}
	grid.push(<FormRow donne={props.donne} start={i * col_size} row_size={props.donne.length - (i * col_size)}/> )
	return (
		grid
	);
}
class Memory extends React.Component
{
  constructor()
  {
	super();
	this.state = {
		card_list: this.load_cards(),
		nb_row: 5,
		nb_col: 6,
	};
  }
  load_cards()
	{
		const fruits = img_loader();
		const cards = [];
		let i = 0;
		fruits.map((fruit)=> {
				cards.push(<Square key={i++} fruit={fruit}/>);
				cards.push(<Square key={i++}fruit={fruit}/>);
				return;
		});
		console.log("set a donne of " + cards.length + " cards");
		console.log("last key aloued:" + i);
//		return shuffle(cards);
		return cards;
}

  render(){
	return (
		<Grid container item xs="auto" spacing={1} direction='row'>
        <Board donne={this.state.card_list}/>
		</Grid>
		)}
}
export default Memory;
