import './Memory.css';

import React from 'react';
import Grid from '@material-ui/core/Grid';
import Square from './Square'
import img_loader from './images'


function FormRow(props) {

    var row = [];
    for (let i = 0; i < props.row_size; i++)
    {
      row.push(<Square fruit={props.fruit}/>)
    }
	return (
		<React.Fragment>
		{row}
		</React.Fragment>
	);
}
function shuffleArray(array) {
	let i = array.length - 1;
	for (; i > 0; i--) {
	  const j = Math.floor(Math.random() * (i + 1));
	  const temp = array[i];
	  array[i] = array[j];
	  array[j] = temp;
	}
	return array;
  }

function Board(props)
{
	const donne = shuffleArray(props.donne);

	return donne;
}
class Memory extends React.Component
{
  constructor()
  {
	super();
	this.state = {
		card_list: this.load_cards(),
		nb_row: 5,
		nb_col: 6,
	};
  }
  load_cards()
	{
		const fruits = img_loader();
		const card = fruits.map((fruit)=> 
			<square fruit={fruit}/>)
		return card;
	}

  render(){
    return (
        <Board donne={this.state.card_list}/>
		)}
}
export default Memory;
