import React from 'react';
import API from '../../utils/API';
import './Header.css';

class Header extends React.Component
{
	constructor(props)
	{
		super(props);
		this.state = {
			user: null,
		}
	};
	async componentDidMount()
	{
		this.setState({user: await API.getUserInfo()});
	}
	render(){
		console.log("Refresh Header");
		return(
		<header>
			<h1>Memory</h1>
			user:{this.user}
		</header>
	)}
}

export default Header;
