import axios from "axios";
const headers = {
	"Content-Type": "application/json"
};
const burl = "http://localhost:8800";

function login(name, password) {
	//create a POST http request
	return axios.post(
		`${burl}/login`,
		{
			name,
			password
		},
		{
			headers: headers
		}
	);
}
function signup(send) {
	return axios.post(`${burl}/signup`, send, { headers: headers });
}

function isAuth() {
	console.log("in Auth: " + localStorage.getItem("token"));
	const ret = localStorage.getItem("token") !== null ;
	console.log("IsAuth: " + ret);
	return ret;
}
function logout() {
	localStorage.clear();
	window.location = "/game";
}
function getToken() {
	return localStorage.getItem("token");
}
function getUserInfo() {
	console.log("get user data start");
	if (this.isAuth){
		console.log("Is log: true");
		let tk = this.getToken();
		console.log("tk: " + tk);
		return axios.put(`${burl}/info`, {token: tk}, {headers: headers}).catch(error => {
			if (error.response && error.response.status === 408)
			{
				console.log("Token expired");
				logout();
			}
		});
	}
	return null;
}
const API = {
	signup,
	login,
	logout,
	isAuth,
	getToken,
	getUserInfo,
}
export default API;
