import './Memory.css';

import React from 'react';
import Grid from '@material-ui/core/Grid';
import Square from './Square'
import img_loader from './images'
import PropTypes from 'prop-types'


function DispCase({fruit, onClick})
{
	return (
		<Square key={fruit.k} fruit={fruit} onClick={() => onClick(fruit)}/> 
	);
}
function FormRow({start, row_size, donne, onClick}) {

    var row = [];
    for (let i = start; i < start + row_size; i++)
    {
	  row.push(<DispCase fruit={donne[i]} onClick={onClick} />)
    }
	return (
		<Grid container item xs="auto" spacing={2} direction='row'>
		{row}
		</Grid>
	);
}
FormRow.defaultProps = {

}
FormRow.propTypes = {
	start: PropTypes.number,
}
function shuffleArray(array) {
	let i = array.length - 1;
	for (; i > 0; i--) {
	  const j = Math.floor(Math.random() * (i + 1));
	  const temp = array[i];
	  array[i] = array[j];
	  array[j] = temp;
	}
	return array;
  }
  function shuffle(array) {
	var currentIndex = array.length, temporaryValue, randomIndex;

	// While there remain elements to shuffle...
	while (0 !== currentIndex) {

	  // Pick a remaining element...
	  randomIndex = Math.floor(Math.random() * currentIndex);
	  currentIndex -= 1;

	  // And swap it with the current element.
	  temporaryValue = array[currentIndex];
	  array[currentIndex] = array[randomIndex];
	  array[randomIndex] = temporaryValue;
	}

	return array;
  }

function Board({donne, onClick})
{
	const col_size = Math.ceil(Math.sqrt(donne.length));
	console.log("col size = " + col_size);
	console.log("donne lenght = " + donne.length);
	const grid = [];
	let i = 0;
	for (; i < col_size - 1; i++)
	{
		grid.push(<FormRow key={i} donne={donne} start={i * col_size} row_size={col_size} onClick={onClick}/>);
		console.log("new row "+ i);
	}
	console.log("after loop: i=" + i + " et col_size = " + col_size);
	grid.push(<FormRow donne={donne} start={i * col_size} row_size={donne.length - (i * col_size)}/> )
	return (grid);
}

class Memory extends React.Component
{
  constructor(props)
  {
	super(props);
	this.state = {
		card_list: this.load_cards(),
		nb_row: 5,
		nb_col: 6,
	};
	this.clickOnCard.bind(this);
  }

	clickOnCard(fruit)
  {
	console.log("click on: " + fruit.alt);
	const lst = this.state.card_list.slice(); 
	console.log("k = " + fruit.k);
	const ndx = lst.findIndex(f => f.k === fruit.k);
	console.log("ndx = " + ndx + " lst = " + lst);
	lst[ndx].visible = true;
	this.setState({
		card_list: lst,
	})
  }

  load_cards()
	{
		const fruits = img_loader();
		const dup = [... fruits];
		const cards = [...fruits, ...dup];
		console.log("set a donne of " + cards.length + " cards");
		//console.log("A: k =" + cards[0].k  + " id =" + cards[0].id);
		//console.log("B: k =" + cards[1].k  + " id =" + cards[1].id);
		console.log(cards);
		return shuffle(cards);
//		return cards;
}

  render(){
	return (
		<div className="game_field">
			<Grid container item xs="auto" spacing={1} direction='column'>
    	    	<Board donne={this.state.card_list} onClick={(i) => this.clickOnCard(i)}/>
			</Grid>
		</div>
		)}
}
export default Memory;
