import './Memory.css';
import Board from '../Board/Board.js'
import InfoDisplay from "../InfoDisplay/InfoDisplay.js"
import React from 'react';
import img_loader from './images/images'
import {Button} from 'react-bootstrap';
import ProgressBar from "../ProgressBar/ProgressBar.js"
import {LinearProgress} from '@material-ui/core';
import { withStyles } from '@material-ui/core/styles';
import API from "../../utils/API.js"
//mélange de facon aléatoire le tableau passé en entrée
function shuffle(array) {
	var currentIndex = array.length, temporaryValue, randomIndex;

	// While there remain elements to shuffle...
	while (0 !== currentIndex) {

	  // Pick a remaining element...
	  randomIndex = Math.floor(Math.random() * currentIndex);
	  currentIndex -= 1;

	  // And swap it with the current element.
	  temporaryValue = array[currentIndex];
	  array[currentIndex] = array[randomIndex];
	  array[randomIndex] = temporaryValue;
	}

	return array;
  }
//TODO  Add Start timer on first click

class Memory extends React.Component
{
	constructor(props)
	{
		super(props);
		this.name = "Memory";
		this.load_cards = this.load_cards.bind(this);
		this.state = {
			card_list: {},
			nb_pair: 0,
			pair_left: -1,
			locked: false,
			flipped: [],
			percent: 1,
			time_left: 180,
			game_state: null,
			timer: null,
			user: {},
		};
		this.startTimer = this.startTimer.bind(this);
		this.countDown = this.countDown.bind(this);
		this.clickOnCard.bind(this);
		this.close_cards = this.close_cards.bind(this);
		this.TimerLinearProgress = withStyles(() => ({
			root: {
			  height: 20,
			  marginTop: 30,
			  borderRadius: 5,
			},
			colorPrimary: {
			  backgroundColor: "#ABCDEF",
			},
			bar: {
			  borderRadius: 5,
			  backgroundColor: '#1a90ff',
			  transition: "5s ease",
			},

		}))(LinearProgress);
	}
	async componentDidMount()
	{
		const res = await API.getUserInfo();
		{res && this.setState({user: res.data})}
		this.startGame();
	}
	//Game state
	startGame()
	{
		console.log("START GAME");
		this.setState({
			game_state: 0,
			card_list: this.load_cards(),
			nb_pair: this.props.location.data,
			pair_left: this.state.card_list.length / 2,
			time_left: 180,
			percent: 1,
		});
		this.startTimer();
		this.setState({ state: this.state });
	}
	endGame()
	{
		//fin du jeu redi
		const res = (this.state.pair_left === 0 ? "win" : "loose" );
		API.sendScore(res);
		return null;
	}
	/*
	loosing()
	{
		console.log("before: " + this.state.timer);
		clearInterval(this.state.timer);
		console.log("after: " + this.state.timer);
		this.sendScore("loose");
		return (
		<div className="loose">
			<h1>You loose...</h1>
			<Button onClick={() => this.startGame()} block>
			Retry?
			</Button>
		</div>);
	}

	winning()
	{
		if (this.state.game_state !== 1)
		{
			this.setState({game_state: 1});
			clearInterval(this.state.timer);
			console.log(" res timer= " + this.timer);
			this.sendScore("win");

		}
		return (
		<div className="loose">
			<h1>You WIN</h1>
			<Button onClick={() => this.startGame()} block>
				Retry?
			</Button>
		</div>);
	}*/
	sendScore(res)
	{
		API.sendScore(res);
	}
	//Jeu
	test_match(flip)
	{
		if (this.getFruit(flip[0]).id === this.getFruit(flip[1]).id)
		{
			console.log("Match!");
			return true;
		}
		console.log("No__ match");
		return false;

	}
	updateFruit(fruit)
	{
		//récup une copie de liste de fruit
		const lst = [...this.state.card_list];
		//récupere l'index du fruit à changer
		const ndx = this.getFruitIndex(fruit);
		//met a jour le fruit dans la liste
		lst[ndx] = fruit;
		//update state
		this.setState({card_list: lst})
	}

	getFruitIndex(fruit)
	{
		return this.state.card_list.findIndex(f => f.k === fruit.k);
	}
	getFruit(ndx)
	{
		return this.state.card_list[ndx];
	}
	close_cards(flip, timer)
	{
		console.log("gF" + this);
		//cache les cartes visibles
		flip.forEach((ndx)=> {
			console.log("hide ndx:" + ndx);
			const fruit = this.getFruit(ndx);
			fruit.visible = false;
			this.updateFruit(fruit);
		});
		this.setState({flipped: [], locked: false});
		clearTimeout(timer);
	}

	match()
	{
		// reset
		this.setState({
			pair_left: this.state.pair_left - 1,
			flipped: []});
	}
	clickOnCard(fruit)
	{
		const flip = [...this.state.flipped];
		if (fruit.visible || this.state.locked)
			return;
		console.log("set visible: key=" + fruit.k + " et id=" + fruit.id);
		//set fruit visible
		fruit.visible = true;
		this.updateFruit(fruit);
		//save flipped card
		flip.push(this.getFruitIndex(fruit));
		//if pair is flipped
		if (flip.length === 2)
		{
			if (this.test_match(flip) === true)
			{
				this.match();
			}
			else
			{
				//set timer for keeping cards visible before hidding
				this.setState({locked: true});
				var timer = setTimeout(this.close_cards, 500, flip, timer);
			}
		}
		else
			this.setState({flipped: flip});

		console.log("quit click");
	}

	load_cards()
	{
		console.log(this.state);
		console.log("Nb pair receive:" + this.state.nb_pair.bind(this));
		const fruits = img_loader(this.state.nb_pair);
		console.log("Nb fruit Loaded:" + fruits.length);
		let dup = [];
		//fill dup with a copy a fruits
		fruits.forEach(f => 
			dup.push({...f}));
		//set l'id pour chaque fruit
		dup.forEach(elem => elem.k = elem.id + dup.length);
		//cards == fruits + dup
		const cards = [...fruits, ...dup];
		console.log("set a donne of " + cards.length + " cards");
		console.log(cards);
		return shuffle(cards);
	}
	//Timer
	startTimer() {
		  this.setState({timer:  setInterval(this.countDown, 1000)}, () => {
			console.log("START TIMER: " + this.state.timer);
		  });
		}
	countDown() {
		// Remove one second, set state so a re-render happens.
		let seconds_left = this.state.time_left - 1;
		console.log("-timeleft:" + seconds_left);
		this.setState({
		  time_left: seconds_left,
		  percent: this.state.time_left / 180,
		});
		// Check if we're at zero.
		if (seconds_left === 0) {
			console.log("TIME'S UP !!!!!");
			this.setState({game_state: -1});
			clearInterval(this.state.timer);
		}
	  }

	updateProgress = (field, val) => {
		this.setState({ [field]: val });
	};

	render(){
		//console.log("paire restante: " + this.state.pair_left);
		//console.log("game status: " + this.state.game_state);
		// to init pair_left
		if (this.state.pair_left === 0 || this.state.game_state === -1)
			return this.endGame();
		else
			return (
					<div className="game_field">
						<div className="board">
	    		    		<Board donne={this.state.card_list} onClick={(i) => this.clickOnCard(i)}/>
							<this.TimerLinearProgress variant="determinate" value={this.state.percent * 100} />
						</div>
						<Button onClick={() => this.setState({pair_left: 0})} >END GAME</Button>

						<InfoDisplay user={this.state.user}/>
				    </div>		
				)};

}
/**
 * 	<ProgressBar width={800} percent={this.state.percent}/>
 * 
 */
export default Memory;
