import './Memory.css';
import Board from './Board'
import React from 'react';
import img_loader from './images'
import PropTypes from 'prop-types'



class Memory extends React.Component
{
	constructor(props)
	{
		super(props);
		this.state = {
			card_list: this.load_cards(),
			flipped: [],
			nb_row: 5,
			nb_col: 6,
		};
		this.clickOnCard.bind(this);
	}
	test_match()
	{
		if (this.state.flipped[0].id === this.state.flipped[1].id)
		{
			console.log("Match!");
			return true;
		}
		console.log("No__ match");
		return flase;

	}

	clickOnCard(fruit)
	{
		if (fruit.visible)
		{
			return;
		}
		const lst = this.state.card_list.slice(); 
		const ndx = lst.findIndex(f => f.k === fruit.k);
		lst[ndx].visible = true;
		console.log("set visible: key=" + fruit.k + " et id=" + fruit.id);
		this.setState({
			card_list: lst,
			flipped: [this.state.flipped, fruit],
		})
		this.test_match();
	}

load_cards()
	{
		const fruits = img_loader();
		let dup = [];
		fruits.forEach(f => 
			dup.push({...f}));
		dup.forEach(elem => elem.k = elem.id + dup.length);
		const cards = [...fruits, ...dup];
		console.log("set a donne of " + cards.length + " cards");
		console.log(cards);
		return cards;
//		return cards;
}

  render(){
    console.log("flipped :" + this.state.flipped);

	return (
		<div className="game_field">
    	    	<Board donne={this.state.card_list} onClick={(i) => this.clickOnCard(i)}/>
		</div>
		)}
}
export default Memory;
