import './Memory.css';
import Board from '../Board/Board.js'
import React from 'react';
import img_loader from './images/images'
import PropTypes from 'prop-types'
import ProgressBar from "../ProgressBar/ProgressBar.js"
import Axios from 'axios';

//mélange de facon aléatoire le tableau passé en entrée
function shuffle(array) {
	var currentIndex = array.length, temporaryValue, randomIndex;

	// While there remain elements to shuffle...
	while (0 !== currentIndex) {

	  // Pick a remaining element...
	  randomIndex = Math.floor(Math.random() * currentIndex);
	  currentIndex -= 1;

	  // And swap it with the current element.
	  temporaryValue = array[currentIndex];
	  array[currentIndex] = array[randomIndex];
	  array[randomIndex] = temporaryValue;
	}

	return array;
  }

class Memory extends React.Component
{
	constructor(props)
	{
		super(props);
		this.name = "Memory"
		this.state = {
			card_list: this.load_cards(),
			pair_left: -1,
			locked: false,
			flipped: [],
			percent: 0,
			time_left: 60,
		};
		this.timer = null;
		this.startTimer = this.startTimer.bind(this);
		this.countDown = this.countDown.bind(this);
		this.clickOnCard.bind(this);
		this.close_cards = this.close_cards.bind(this);
	}
	//Jeu
	test_match(flip)
	{
		if (this.getFruit(flip[0]).id === this.getFruit(flip[1]).id)
		{
			console.log("Match!");
			return true;
		}
		console.log("No__ match");
		return false;

	}
	updateFruit(fruit)
	{
		//récup une copie de liste de fruit
		const lst = [...this.state.card_list];
		//récupere l'index du fruit à changer
		const ndx = this.getFruitIndex(fruit);
		//met a jour le fruit dans la liste
		lst[ndx] = fruit;
		//update state
		this.setState({card_list: lst})
	}

	getFruitIndex(fruit)
	{
		return this.state.card_list.findIndex(f => f.k === fruit.k);
	}
	getFruit(ndx)
	{
		return this.state.card_list[ndx];
	}
	close_cards(flip, timer)
	{
		console.log("gF" + this);
		//cache les cartes visibles
		flip.forEach((ndx)=> {
			console.log("hide ndx:" + ndx);
			const fruit = this.getFruit(ndx);
			fruit.visible = false;
			this.updateFruit(fruit);
		});
		this.setState({flipped: [], locked: false});
		clearTimeout(timer);
	}

	match()
	{
		// reset
		this.setState({
			pair_left: this.state.pair_left - 1,
			flipped: []});
	}
	clickOnCard(fruit)
	{
		const flip = [...this.state.flipped];
		if (fruit.visible || this.state.locked)
			return;
		console.log("set visible: key=" + fruit.k + " et id=" + fruit.id);
		//set fruit visible
		fruit.visible = true;
		this.updateFruit(fruit);
		//save flipped card
		flip.push(this.getFruitIndex(fruit));
		//if pair is flipped
		if (flip.length === 2)
		{
			if (this.test_match(flip) === true)
			{
				this.match();
			}
			else
			{
				//set timer for keeping cards visible before hidding
				this.setState({locked: true});
				var timer = setTimeout(this.close_cards, 800, flip, timer);
			}
		}
		else
			this.setState({flipped: flip});

		console.log("quit click");
	}

	load_cards()
	{
		const fruits = img_loader();
		let dup = [];
		fruits.forEach(f => 
			dup.push({...f}));
		dup.forEach(elem => elem.k = elem.id + dup.length);
		const cards = [...fruits, ...dup];
		console.log("set a donne of " + cards.length + " cards");
		console.log(cards);
		return shuffle(cards);
	}
	//Timer
	startTimer() {
		if (this.timer === null) {
		  this.timer = setInterval(this.countDown, 1000);
		}
	  }
	  countDown() {
		// Remove one second, set state so a re-render happens.
		let seconds_left = this.state.time_left - 1;
		console.log("-timeleft:" + seconds_left);
		this.setState({
		  time_left: seconds_left,
		  percent: this.state.time_left / 60,
		});
		// Check if we're at zero.
		if (seconds_left === 0) {
			console.log("TIME'S UP !!!!!");
			clearInterval(this.timer);
			API.looseTheGame();
		}
	  }

	updateProgress = (field, val) => {
		this.setState({ [field]: val });
	};

	render(){
		console.log("paire restante: " + this.state.pair_left);
		this.startTimer();
		// to init pair_left
		if (this.state.pair_left === -1)
			this.setState({pair_left: this.state.card_list.length / 2});
		if (this.state.pair_left === 0)
			return (<h1>You win</h1>);
		else
			return (
				<React.Fragment>
					<div className="game_field">
    		    		<Board donne={this.state.card_list} onClick={(i) => this.clickOnCard(i)}/>
						<div>
						<ProgressBar width={400} percent={this.state.percent}/>
				</div>
					</div>
					</React.Fragment>
				)};

}
export default Memory;
