import "./Menu.css";
import API from "../../utils/API.js"

import {useState, useEffect} from 'react'
import {Link} from 'react-router-dom'
import Button from '@material-ui/core/Button'
import Radio from '@material-ui/core/Radio'
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControl from '@material-ui/core/FormControl'
import FormControlLabel from '@material-ui/core/FormControlLabel'
import FormLabel from '@material-ui/core/FormLabel'
import ScoreList from "../ScoreList/ScoreList.js"

const fetchData = async (func) => {
	const result = await func;
	console.log(result);
	// return the result
	return result;
  };

function Menu()
{
	const [nb_card, setNbCard] = useState("5");
	const [scores_list, setScoresList] = useState([]);

	const handleChange = (event) => {
		console.log("New nb card =" + event.target.value);
		setNbCard(event.target.value);
	  };
	  useEffect(() => {
		async function getScores() {
			const scores = await API.getScores("user");
			setScoresList(scores);
		}
		getScores();
	 }, []);

	console.log("MENU");
	console.log("Score => ");
	console.log(scores_list);
	return (
		<div class="extern">
			<div class="intern">
				<h2>Menu</h2>
				<FormControl component="fieldset">
					<FormLabel component="legend">Nombre de paires:</FormLabel>
