import React from "react";
import { Button, FormGroup, FormControl, ControlLabel } from "react-bootstrap";
import API from "../../utils/API";

class Signup extends React.Component {
	state = {
		name: "",
		password: "",
		cpassword: "",
		error_msg: "",
	};
	send = async () => {
		const { name, password, cpassword } = this.state;

		//vérification coté client
		if (!name || name.length === 0) return;
		if (!password || password.length === 0) return;
		if (password !== cpassword) {this.setState({error_msg: "Erreur lors de l'envoi de donnée"});return};
		try {
			const { data } = await API.signup({ name, password });
			localStorage.setItem("token", data.token);
			window.location = "/menu";
		} catch (error) {
			if (error.response)
			{
				if (error.response.status === 400)
					this.setState({error_msg: "Erreur lors de l'envoi de donnée"});
				else if (error.response.status === 401)
					this.setState({error_msg: "L'utilisateur ou le mot de passe est incorrect"});
				return;
			}
			else
				console.error(error);
		}
	};
  handleChange = (event) => {
	this.setState({
	  [event.target.id]: event.target.value
	});
  };
  render() {
	const { name, password, cpassword } = this.state;
	return (
	  <div className="Login">
		  <h2>Signup</h2>
			<span>{this.state.error_msg}</span>
		<FormGroup controlId="name" bsSize="large">
		  <ControlLabel>Name</ControlLabel>
		  <FormControl
			autoFocus
			type="name"
			value={name}
			onChange={this.handleChange}
		  />
		</FormGroup>
		<FormGroup controlId="password" bsSize="large">
		  <ControlLabel>Password</ControlLabel>
		  <FormControl
			value={password}
			onChange={this.handleChange}
			type="password"
		  />
		</FormGroup>
		<FormGroup controlId="cpassword" bsSize="large">
		  <ControlLabel>Confirm Password</ControlLabel>
		  <FormControl
			value={cpassword}
			onChange={this.handleChange}
			type="password"
		  />
		</FormGroup>
		<Button onClick={this.send} block bsSize="large" type="submit">
		  Inscription
		</Button>
	  </div>
	);
  }
}
export default Signup;
